SELECT product_category, SUM(order_amount) AS total_sales
FROM orders
GROUP BY product_category;

CREATE TABLE customers (
  customer_id INT,
  name VARCHAR(50),
  city VARCHAR(30)
);

SELECT name, amount,
RANK() OVER (ORDER BY amount DESC) AS rank
FROM orders;

SELECT campaign_type, AVG(conversion_rate)
FROM campaigns
GROUP BY campaign_type;

SELECT month, SUM(revenue - expense) AS profit
FROM finance
GROUP BY month;
