class Main {
  public static void main(String[] args) {
    System.out.println("Hello Java");
  }
}

boolean hasUsername = true;
boolean hasPassword = false;

System.out.println(hasUsername && hasPassword); // AND
System.out.println(hasUsername || hasPassword); // OR
System.out.println(!hasPassword);               // NOT

int balance = 1000;
balance += 500;  // balance = balance + 500
balance -= 200;  // balance = balance - 200

System.out.println(balance);

int score = 10;

System.out.println(++score); // Pre-increment
System.out.println(--score); // Pre-decrement

int marks = 60;
String result = (marks >= 40) ? "Pass" : "Fail";
System.out.println(result);

int age = 18;

if (age >= 18) {
  System.out.println("Eligible to vote");
}

int marks = 35;

if (marks >= 40) {
  System.out.println("Pass");
} else {
  System.out.println("Fail");
}

int day = 3;

switch (day) {
  case 1:
    System.out.println("Monday");
    break;
  case 2:
    System.out.println("Tuesday");
    break;
  case 3:
    System.out.println("Wednesday");
    break;
  default:
    System.out.println("Invalid day");
}

for (int i = 1; i <= 5; i++) {
  System.out.println(i);
}

int i = 1;

while (i <= 3) {
  System.out.println(i);
  i++;
}

int i = 5;

do {
  System.out.println(i);
  i++;
} while (i < 3);

for (int i = 1; i <= 5; i++) {
  if (i == 3) {
    break;
  }
  System.out.println(i);
}

for (int i = 1; i <= 5; i++) {
  if (i == 3) {
    continue;
  }
  System.out.println(i);
}

