class Student {
  constructor(name, age) {
    this.name = name;
    this.age = age;
  }

  greet() {
    return `Hello, my name is ${this.name}`;
  }
}

const student1 = new Student("Ritik", 21);
const student2 = new Student("Amit", 22);

console.log(student1.greet());
console.log(student2.greet());

class BankAccount {
  #balance = 0; // private variable

  deposit(amount) {
    if (amount > 0) {
      this.#balance += amount;
    }
  }

  getBalance() {
    return this.#balance;
  }
}

const acc = new BankAccount();
acc.deposit(5000);
console.log(acc.getBalance());

class Person {
  constructor(name) {
    this.name = name;
  }
}

class Employee extends Person {
  constructor(name, role) {
    super(name);
    this.role = role;
  }
}

const emp = new Employee("Amit", "Developer");
console.log(emp.name, emp.role);

class Animal {
  sound() {
    console.log("Animal makes a sound");
  }
}

class Dog extends Animal {
  sound() {
    console.log("Dog barks");
  }
}

const animal = new Animal();
const dog = new Dog();

animal.sound();
dog.sound();

class Vehicle {
  start() {
    throw new Error("Method must be implemented");
  }
}

class Car extends Vehicle {
  start() {
    console.log("Car started");
  }
}

const car = new Car();
car.start();

class User {
  constructor(username) {
    this.username = username;
  }
}

const user1 = new User("admin");
console.log(user1.username);

class Laptop {
  constructor(brand) {
    this.brand = brand;
  }

  showBrand() {
    console.log(this.brand);
  }
}

const lap = new Laptop("HP");
lap.showBrand();

class Shape {
  constructor(color) {
    this.color = color;
  }
}

class Circle extends Shape {
  constructor(color, radius) {
    super(color);
    this.radius = radius;
  }
}

const c = new Circle("Red", 10);
console.log(c.color, c.radius);

